if ( flow.hiTemperatureAlert < msg.payload.values.t ) {
    if ( !flow.triggeredTemperatureHigh ) {
        flow.triggeredTemperatureHigh = true;
        flow.triggeredTemperatureOk = false;
        node.send({
            to: "ray@oneunified.net",
            from: msg.payload.device + "@oneunified.net",
            topic: msg.payload.device + " temperature high alert",
            payload: msg.payload
        })
    }
}

if ( !flow.triggeredTemperatureOk ) {
    if ( ( flow.hiTemperatureOk > msg.payload.values.t )
      && ( flow.loTemperatureOk < msg.payload.values.t )
    ) {
        flow.triggeredTemperatureHigh = false;
        flow.triggeredTemperatureOk = true;
        flow.triggeredTemperatureLow = false;
        node.send({
            to: "ray@oneunified.net",
            from: msg.payload.device + "@oneunified.net",
            topic: msg.payload.device + " temperature return to normal",
            payload: msg.payload
        })
    }
}

if ( flow.loTemperatureAlert > msg.payload.values.t ) {
    if ( !flow.triggeredTemperatureLow ) {
        flow.triggeredTemperatureLow = true;
        flow.triggeredTemperatureOk = false;
        node.send({
            to: "ray@oneunified.net",
            from: msg.payload.device + "@oneunified.net",
            topic: msg.payload.device + " temperature low alert",
            payload: msg.payload
        })
    }
}

